<script>
	import Button from './UI/Button.svelte';
	import MeetupGrid from "./Meetups/MeetupGrid.svelte";
	import Header from "./UI/Header.svelte";
	import TextInput from "./UI/TextInput.svelte";
	
	let title='';
	let subtitle='';
	let address='';
	let email ='';
	let description='';
	let imageUrl='';


	let meetups = [
		{
			id: "m1",
			title: "Coding Bootcamp",
			subtitle: "Learn to code in 2 hours!",
			description:
				"In this meetup, we will have some experts to teach you!",
			imageUrl:
				"data:image/jpeg;base64,",
			address: "Four Season Total Landscape",
			contactEmail: "code@test.com",
		},
		{
			id: "m2",
			title: "Cycling through Amsterdam!",
			subtitle: "Let's cycle around the city!",
			description: "In this meetup, we will cycle and meet places",
			imageUrl:
				"https://upload.wikimedia.org/wikipedia/commons/f/fa/Amsterdam_-_Bicycles_-_1058.jpg",
			address: "Bilderdijkkade 68II, 1053 VN Amsterdam, Netherlands",
			contactEmail: "cycle@test.com",
		},
	];
	function addMeetup() {

		const newMeetup = {
			id: Math.random().toString(),
			title: title,
			substitle: subtitle,
			description: description,
			imageUrl: imageUrl, 
			contactEmail: email,
			address: address,
			description: description
		};
		meetups = [newMeetup,...meetups];
	}

</script>

<style>
	main {
		margin-top: 5rem;
	}

	form{
		width: 30rem;
		max-width: 90%;
		margin: auto;
	}
</style>

<Header />

<main>
	<form on:submit|preventDefault={addMeetup}>
		<TextInput 
		id="title"
		label="Title"
		value={title}

		on:input={event=> (title = event.target.value)}/>
		<TextInput 
		id="subtitle"
		label="Subtitle"
		
		value={subtitle}
		on:input={event=> (subtitle = event.target.value)}/>
		<TextInput 
		id="address"
		label="Address"
		
		value={address}
		on:input={event=> (address = event.target.value)}/>
		<TextInput 
		id="imageUrl"
		label="ImageUrl"
		value={imageUrl}
	
		on:input={event=> (imageUrl = event.target.value)}/>
		<TextInput 
		id="email"
		label="E-Mail"
		
		value={email}
		on:input={event=> (email = event.target.value)}/>
		<TextInput 
		id="description"
		label="Description"
		type="textarea"
		value={description}
		on:input={event=> (description = event.target.value)}/>
		<Button type="submit" caption="Save" />
	</form>
	<MeetupGrid {meetups} />
</main>
